mapper.select:
select a.capitalid, a.empid, a.ordid, a.projectsid, r.value, a.capital, a.time from capital a,capitaltype r
a.capitalflow=r.key
and a.capitalid= #{capitalid}
and a.empid= #{empid}
and a.ordid= #{ordid}
and a.projectsid= #{projectsid}
and a.capitalflow= #{capitalflow}
and a.time >= to_date(#{starttime},'yyyy-MM-dd HH24:MI:SS')
and a.time < = to_date(#{endtime},'yyyy-MM-dd HH24:MI:SS')
order by capitalid desc

mapper.insert:
INSERT INTO capital (capitalid,empid, ordid, projectsid, capitalflow, capital,time) SELECT list_capital.NEXTVAL capitalid, A.* FROM(
SELECT #{item.EMPID,jdbcType=NUMERIC}, #{item.ORD_ID,jdbcType=NUMERIC}, #{item.PROJECTSID,jdbcType=NUMERIC}, 3, #{item.PAYMONEY,jdbcType=VARCHAR}, sysdate FROM dual
)A
