mapper.select:
	select 
		a.ord_id,
		a.empid,
		a.fraction,
		a.projectsid,
		a.paymoney,
		r.name,
		r.phone,
		r.addressinfo,
		a.pro_returnid,
		a.order_time,
		w.value
 	from orders a,address r ,word w 

 		 a.address=r.addressid 	and w.key=a.ord_status and e.empid=a.empid

 			and a.ord_id= #{ord_id}


 			and a.empid= #{empid}


 			and a.ordid= #{ordid}


 			and a.projectsid= #{projectsid}


 			and a.paymoney= #{paymoney}


 			and a.order_time= #{order_ime}


 			and a.pro_returnid= #{proreturnid}
