mapper.select:
	select 
		a.ord_id,
		a.empid,
		a.fraction,
		a.projectsid,
		a.paymoney,
		a.pro_returnid,
		a.order_time
 	from orders a


 			 a.empid= #{empid}


 			and a.ord_id= #{ordid}


 			and a.projectsid= #{projectsid}


 			and a.paymoney= #{paymoney}


 			and a.order_time= #{order_ime}


 			and a.pro_returnid= #{proreturnid}


 		 	order by ord_id desc

mapper.update:
update orders


		 ord_status=#{ordstatus,jdbcType=NUMERIC},





			ord_id=#{ordid}


		and projectsid=#{projectsid}
