mapper.select:
	select 
		a.profitid,
		a.projectsid,
		a.capitalflow,
		c.value,
		a.capital,
		a.time
 	from profit a ,capitaltype c,

 		c.key=a.capitalflow

 			 a.profitid= #{profitid}


 			and a.projectsid= #{ordid}


 			and a.capitalflow= #{projectsid}


 			and a.capital= #{paymoney}


 			and a.time >= to_date(#{starttime},'yyyy-MM-dd HH24:MI:SS')


 			and a.time < = to_date(#{endtime},'yyyy-MM-dd HH24:MI:SS')


 		 	order by profitid desc
